{
  "version": "1.0.1",
  "layers": [
    {
      "type": "cartodb",
      "options": {
        "sql": "select * from world_borders",
        "cartocss": "\/** simple visualization *\/\n\n#world_borders{\n  polygon-fill: #3E7BB6;\n  polygon-opacity: 0.7;\n  line-color: #FFF;\n  line-width: 0.5;\n  line-opacity: 1;\n}",
        "cartocss_version": "2.1.1",
        "interactivity": [
          "cartodb_id"
        ]
      }
    }
  ]
}

{
  "auth": {
    "method": "token",
    "valid_tokens": [
    	"cartodb",
    	"carto"
    	]
  },
  "version": "0.0.1",
  "name": "named_map_test",
  "placeholders": {
    "filter": {
      "type": "number",
      "default": 2
    },
    "color": {
      "type": "css_color",
      "default": "#FF0000"
    }
  },
  "layergroup": {
    "version": "1.0.1",
    "layers": [
      {
        "type": "cartodb",
        "options": {
          "sql": "select * from world_borders",
          "layer_name": "world_borders",
          "cartocss": "#layer { polygon-fill: lighten(red,30); polygon-opacity: 0.1; line-color: #F00; }",
          "cartocss_version": "2.1.1",
          "interactivity": [
            "cartodb_id",
            "name"
          ],
          "table_name": "\"\".",
          "attributes": {
            "id": "cartodb_id",
            "columns": [
              "name",
              "region",
              "iso2",
              "iso3",
              "pop2005"
            ]
          }
        }
      },
      {
        "type": "cartodb",
        "options": {
          "sql": "select * from ne_10m_populated_places_simple where pop_max > <%= filter %>",
          "layer_name": "populated_places",
          "cartocss": "#populated_places{ marker-line-opacity: 0;  marker-width: 3;  marker-fill: <%= color %>; marker-allow-overlap: true;}",
          "cartocss_version": "2.1.1",
          "interactivity": [
            "cartodb_id",
            "name"
          ],
          "table_name": "\"\".",
          "attributes": {
            "id": "cartodb_id",
            "columns": [
              "adm0name",
              "name"
            ]
          }
        }
      }
    ]
  },
  "view": {
    "zoom": 3,
    "center": {
      "lng": 0,
      "lat": 0
    },
    "bounds": {
      "west": -80,
      "south": -40,
      "east": 80,
      "north": 40
    }
  }
}

