{
	"query": [
		"create table secciones_evolucion_bcn as with data as (select s.cartodb_id, ST_Centroid(s.the_geom_webmercator) as the_geom_webmercator, ST_Centroid(s.the_geom) as the_geom, e.m_ener_1506, e.m_ener_1507, e.m_ener_1508, e.m_ener_1509, e.m_ener_1510, e.m_ener_1511, e.m_ener_1512, e.m_ener_1601, e.m_ener_1602, e.m_ener_1603, e.m_ener_1604, e.m_ener_1605, e.m_ener_1606 from evolucion e join secciones s on s.area_id = e.ucensal_ps and s.the_geom && ST_MakeEnvelope(2.056847,41.336349,2.286186,41.472831,4326) limit 1 ) select d.cartodb_id, d.the_geom_webmercator, d.the_geom, d.m_ener_1506 as margen, '2015-06-01'::date as date from data d union all select d.cartodb_id + 1e6 as cartodb_id, d.the_geom_webmercator, d.the_geom, d.m_ener_1507 as margen, '2015-07-01'::date as date from data d union all select d.cartodb_id + 2e6 as cartodb_id, d.the_geom_webmercator, d.the_geom, d.m_ener_1508 as margen, '2015-08-01'::date as date from data d union all select d.cartodb_id + 3e6 as cartodb_id, d.the_geom_webmercator, d.the_geom, d.m_ener_1509 as margen, '2015-09-01'::date as date from data d union all select d.cartodb_id + 4e6 as cartodb_id, d.the_geom_webmercator, d.the_geom, d.m_ener_1510 as margen, '2015-10-01'::date as date from data d union all select d.cartodb_id + 5e6 as cartodb_id, d.the_geom_webmercator, d.the_geom, d.m_ener_1511 as margen, '2015-11-01'::date as date from data d union all select d.cartodb_id + 6e6 as cartodb_id, d.the_geom_webmercator, d.the_geom, d.m_ener_1512 as margen, '2015-12-01'::date as date from data d union all select d.cartodb_id + 7e6 as cartodb_id, d.the_geom_webmercator, d.the_geom, d.m_ener_1601 as margen, '2016-01-01'::date as date from data d union all select d.cartodb_id + 8e6 as cartodb_id, d.the_geom_webmercator, d.the_geom, d.m_ener_1602 as margen, '2016-02-01'::date as date from data d union all select d.cartodb_id + 9e6 as cartodb_id, d.the_geom_webmercator, d.the_geom, d.m_ener_1603 as margen, '2016-07-03'::date as date from data d union all select d.cartodb_id + 10e6 as cartodb_id, d.the_geom_webmercator, d.the_geom, d.m_ener_1604 as margen, '2016-04-01'::date as date from data d union all select d.cartodb_id + 11e6 as cartodb_id, d.the_geom_webmercator, d.the_geom, d.m_ener_1605 as margen, '2016-05-01'::date as date from data d union all select d.cartodb_id + 12e6 as cartodb_id, d.the_geom_webmercator, d.the_geom, d.m_ener_1606 as margen, '2016-06-01'::date as date from data d;",
		"select cdb_cartodbfytable('endesa-admin','secciones_evolucion_bcn');"
		]
